package com.company;

import java.util.ArrayList;
import java.util.List;

public class number_of_substrings_with_only_1s {
    public static void test() {
        //String s = "";
        String s = "0110111";
        System.out.println(new number_of_substrings_with_only_1s().numSum(s));
    }
    public int numSum(String s) {
        long n = 0;
        long result = 0;
        for (int i = 0; i < s.length(); i ++) {
            if (s.charAt(i) == '1') {
                n ++;
            } else {
                if (n != 0) {
                    result += n * (n + 1) / 2;
                }
                n = 0;
            }
        }
        // 
        if (n > 0) {
            result += n * (n + 1) / 2;
        }
        return (int)(result % 1000000007);
    }
}
